use std::str::FromStr;

use rsacracker::{run_attacks, Parameters};
use rug::Integer;

#[test]
fn tjctf_2022_factor_master_stage1() {
    // From TJCTF 2022 / Factor Master - Stage 1
    // https://ctftime.org/task/21337

    let params = Parameters {
        n : Some(Integer::from_str("").unwrap()),
        ..Default::default()

    };

    let solution = run_attacks(&params).unwrap();
    assert!(solution.pk.is_some());
}

